import React, { useState, useEffect, Suspense, lazy } from "react";
import { motion } from "framer-motion";
import { useTheme } from "../hooks/useTheme";
import { SEO } from "./SEO";
import { LazyLoadImage } from "react-lazy-load-image-component";
import "react-lazy-load-image-component/src/effects/blur.css";
import AOS from 'aos';
import 'aos/dist/aos.css';
import { useGitHubProjects } from "../hooks/useGitHubProjects";
import { useContactForm } from "../hooks/useContactForm";

// Componente de Loading
const LoadingFallback = () => (
  <div className="w-full h-full flex items-center justify-center p-4">
    <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-purple-500"></div>
  </div>
);

// Lazy loading dos componentes com tratamento de erro
const ProjectCard = lazy(() => 
  import("./ProjectCard").then(module => ({ default: module.ProjectCard }))
);

const SkillCard = lazy(() => 
  import("./SkillCard").then(module => ({ default: module.SkillCard }))
);

// Animações
const fadeIn = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0 },
  exit: { opacity: 0, y: -20 }
};

const pageTransition = {
  type: "tween",
  ease: "anticipate",
  duration: 0.5
};

// Easter Egg - Konami Code
const konamiCode = ['ArrowUp', 'ArrowUp', 'ArrowDown', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'ArrowLeft', 'ArrowRight', 'b', 'a'];
let konamiIndex = 0;

export const Portfolio = () => {
  const { theme, toggleTheme } = useTheme();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showEasterEgg, setShowEasterEgg] = useState(false);
  const { projects, loading, error, filter, availableFilters, setFilter } = useGitHubProjects('RonaldoChiavegatti');
  const { 
    formData, 
    errors, 
    isSubmitting, 
    submitStatus, 
    handleChange, 
    handleSubmit 
  } = useContactForm();

  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: true,
      mirror: false,
    });
  }, []);

  useEffect(() => {
    const handleKeydown = (e) => {
      if (e.key === konamiCode[konamiIndex]) {
        konamiIndex++;
        if (konamiIndex === konamiCode.length) {
          setShowEasterEgg(true);
          konamiIndex = 0;
        }
      } else {
        konamiIndex = 0;
      }
    };

    window.addEventListener('keydown', handleKeydown);
    return () => window.removeEventListener('keydown', handleKeydown);
  }, []);

  return (
    <>
      <SEO />
      <motion.div 
        className="min-h-screen w-full bg-white dark:bg-neutral-900 text-gray-900 dark:text-gray-100"
        initial="initial"
        animate="animate"
        exit="exit"
        variants={fadeIn}
        transition={pageTransition}
      >
        {showEasterEgg && (
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            className="fixed inset-0 flex items-center justify-center z-50 bg-black/80"
            onClick={() => setShowEasterEgg(false)}
          >
            <motion.div
              animate={{
                rotate: [0, 360],
                scale: [1, 1.2, 1],
              }}
              transition={{
                duration: 1,
                repeat: Infinity,
              }}
              className="text-6xl"
            >
              🎮
            </motion.div>
          </motion.div>
        )}

        <div id="top" className="w-full min-h-screen">
          {/* Header */}
          <header className="sticky top-0 z-50 w-full bg-white/90 dark:bg-neutral-900/90 backdrop-blur-sm border-b border-gray-200 dark:border-neutral-700">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
              <nav className="flex items-center justify-between py-4 md:py-6">
                <h1 className="text-2xl md:text-3xl font-bold font-montserrat">Ronaldo Chiavegatti</h1>

                <button 
                  className="md:hidden p-2 rounded-lg hover:bg-neutral-800/50 dark:hover:bg-gray-100/50 transition-all duration-300"
                  onClick={() => setIsMenuOpen(!isMenuOpen)}
                >
                  <span className="material-symbols-outlined">
                    {isMenuOpen ? 'close' : 'menu'}
                  </span>
                </button>

                <div className={`
                  md:flex items-center gap-6 lg:gap-8
                  ${isMenuOpen 
                    ? 'absolute top-full left-0 right-0 flex flex-col items-center gap-4 py-4 bg-neutral-900/95 dark:bg-white/95 border-b border-neutral-700 dark:border-neutral-200' 
                    : 'hidden md:flex'
                  }
                `}>
                  <a href="#about" className="hover:text-purple-500 dark:hover:text-purple-600 transition-all hover:scale-105 py-2">Sobre</a>
                  <a href="#projects" className="hover:text-purple-500 dark:hover:text-purple-600 transition-all hover:scale-105 py-2">Projetos</a>
                  <a href="#skills" className="hover:text-purple-500 dark:hover:text-purple-600 transition-all hover:scale-105 py-2">Habilidades</a>
                  <a href="#contact" className="hover:text-purple-500 dark:hover:text-purple-600 transition-all hover:scale-105 py-2">Contato</a>
                  <button 
                    onClick={toggleTheme}
                    className="hover:text-purple-500 dark:hover:text-purple-600 transition-all hover:scale-105 p-2"
                  >
                    <span className="material-symbols-outlined">
                      {theme === 'dark' ? 'light_mode' : 'dark_mode'}
                    </span>
                  </button>
                </div>
              </nav>
            </div>
          </header>

          <main className="w-full bg-gradient-to-br from-white via-gray-50 to-white dark:from-neutral-900 dark:via-neutral-800 dark:to-neutral-900">
            {/* Hero Section */}
            <section className="min-h-screen flex items-center justify-center">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 md:py-24">
                <div className="space-y-6 md:space-y-8 text-center md:text-left animate-fadeIn">
                  <h2 className="text-gray-100 dark:text-gray-800 text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold font-poppins leading-tight animate-slideUp">
                    Olá, eu sou <span className="bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent animate-gradient">Ronaldo Chiavegatti</span>
                  </h2>
                  <p className="text-gray-200 dark:text-gray-700 text-lg md:text-xl max-w-3xl mx-auto md:mx-0 animate-slideUp delay-100">
                    Desenvolvedor Full Stack apaixonado por criar experiências web bonitas e funcionais.
                  </p>
                  <a 
                    href="#projects" 
                    className="inline-block px-8 py-4 text-lg bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300 hover:scale-105 shadow-lg hover:shadow-purple-500/20 animate-slideUp delay-200"
                  >
                    Ver Projetos
                  </a>
                </div>
              </div>
            </section>

            {/* About Section */}
            <section id="about" className="py-20 bg-neutral-800/50 dark:bg-gray-200/80 backdrop-blur-sm">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="grid md:grid-cols-2 gap-12 items-center">
                    <div 
                      data-aos="fade-right" 
                      data-aos-duration="1000" 
                      className="order-2 md:order-1"
                    >
                      <LazyLoadImage
                    src="https://via.placeholder.com/600x600" 
                    alt="Profile" 
                        effect="blur"
                        className="w-full max-w-md mx-auto rounded-2xl shadow-lg hover:shadow-xl transition-all duration-500 hover:scale-105 shadow-purple-500/10"
                      />
                    </div>
                    <div 
                      data-aos="fade-left" 
                      data-aos-duration="1000" 
                      data-aos-delay="200" 
                      className="space-y-6 order-1 md:order-2"
                    >
                      <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold font-poppins bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent">
                        Sobre Mim
                      </h2>
                      <div className="space-y-4 text-black dark:text-gray-300">
                        <p className="text-lg leading-relaxed">
                          Sou um desenvolvedor Full Stack apaixonado por criar soluções inovadoras e experiências digitais impactantes. Com formação em desenvolvimento web e experiência em projetos diversos, busco constantemente aprender novas tecnologias e aprimorar minhas habilidades.
                        </p>
                        <p className="text-lg leading-relaxed">
                          Minha jornada na programação começou com HTML, CSS e JavaScript, e desde então venho expandindo meu conhecimento para incluir tecnologias modernas como React, Node.js, e TypeScript. Tenho experiência em desenvolvimento de aplicações web responsivas, APIs RESTful e integração com bancos de dados.
                        </p>
                        <p className="text-lg leading-relaxed">
                          Além do código, valorizo muito o trabalho em equipe, a comunicação clara e a resolução criativa de problemas. Estou sempre em busca de novos desafios que me permitam crescer profissionalmente e contribuir para projetos significativos.
                        </p>
                      </div>
                      <div 
                        className="flex flex-wrap gap-4 pt-4"
                        data-aos="fade-up" 
                        data-aos-duration="800" 
                        data-aos-delay="400"
                      >
                        <a 
                          href="https://github.com/seu-usuario" 
                          className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300 hover:scale-105 shadow-lg hover:shadow-purple-500/20"
                        >
                        <i className="fa-brands fa-github"></i>
                        GitHub
                      </a>
                        <a 
                          href="https://linkedin.com/in/seu-usuario" 
                          className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300 hover:scale-105 shadow-lg hover:shadow-purple-500/20"
                        >
                        <i className="fa-brands fa-linkedin"></i>
                        LinkedIn
                      </a>
                        <a 
                          href="/seu-curriculo.pdf" 
                          className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-300 hover:scale-105 shadow-lg hover:shadow-purple-500/20"
                        >
                          <i className="fa-solid fa-file-pdf"></i>
                          Currículo
                        </a>
                      </div>
                  </div>
                </div>
              </div>
            </section>

            {/* Projects Section */}
            <section id="projects" className="py-20">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                  <div className="text-center space-y-4 mb-12">
                    <h2 
                      className="text-3xl md:text-4xl lg:text-5xl font-bold font-poppins bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent"
                      data-aos="fade-up"
                    >
                      Projetos
                    </h2>
                    <p 
                      className="text-gray-300 dark:text-gray-600 text-lg max-w-2xl mx-auto"
                      data-aos="fade-up"
                      data-aos-delay="100"
                    >
                      Confira alguns dos meus projetos mais recentes no GitHub. Use os filtros abaixo para explorar por tecnologia.
                    </p>
                        </div>

                  {loading ? (
                    <div className="flex justify-center items-center min-h-[400px]">
                      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
                      </div>
                  ) : error ? (
                    <div className="text-center text-red-500 dark:text-red-600 py-12">
                      <p className="text-lg">{error}</p>
                      <p className="text-sm mt-2">Tente novamente mais tarde.</p>
                    </div>
                  ) : (
                    <>
                      <div 
                        className="flex flex-wrap justify-center gap-3 mb-12"
                        data-aos="fade-up"
                        data-aos-delay="200"
                      >
                        {availableFilters.map((filterOption) => (
                          <button
                            key={filterOption}
                            onClick={() => setFilter(filterOption)}
                            className={`px-4 py-2 rounded-full text-sm transition-all duration-300 ${
                              filter === filterOption
                                ? 'bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-lg shadow-purple-500/20'
                                : 'bg-neutral-800/50 dark:bg-gray-200/50 text-gray-300 dark:text-gray-600 hover:bg-neutral-700 dark:hover:bg-gray-300'
                            }`}
                          >
                            {filterOption === 'all' ? 'Todos' : filterOption}
                          </button>
                        ))}
                      </div>

                      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
                        {projects.map((project) => (
                          <Suspense key={project.id} fallback={<LoadingFallback />}>
                            <ProjectCard project={project} />
                          </Suspense>
                        ))}
                      </div>

                      {projects.length === 0 && (
                        <div className="text-center py-12 text-gray-400">
                          <p>Nenhum projeto encontrado com o filtro selecionado.</p>
                        </div>
                      )}
                    </>
                  )}
              </div>
            </section>

            {/* Skills Section */}
            <section id="skills" className="py-20 bg-neutral-800/95 dark:bg-gray-300/95 backdrop-blur-sm">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                  <div className="text-center space-y-4 mb-12">
                    <h2 
                      className="text-3xl md:text-4xl lg:text-5xl font-bold font-poppins bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent"
                      data-aos="fade-up"
                    >
                      Habilidades
                    </h2>
                    <p 
                      className="text-gray-300 dark:text-gray-600 text-lg max-w-2xl mx-auto"
                      data-aos="fade-up"
                      data-aos-delay="100"
                    >
                      Tecnologias e ferramentas que utilizo para criar soluções inovadoras e experiências digitais impactantes.
                    </p>
                    </div>

                  <div 
                    className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-6"
                    data-aos="fade-up"
                    data-aos-delay="200"
                  >
                    {[
                      'HTML',
                      'CSS',
                      'JavaScript',
                      'React',
                      'Node.js',
                      'Python',
                      'Git',
                      'Docker',
                      'AWS',
                      'MongoDB',
                      'TypeScript',
                      'Redux'
                    ].map((skill) => (
                      <Suspense key={skill} fallback={<LoadingFallback />}>
                        <SkillCard skill={skill} />
                      </Suspense>
                  ))}
                </div>
              </div>
            </section>

            {/* Contact Section */}
            <section id="contact" className="py-20">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="text-center space-y-4 mb-12">
                  <h2 
                    className="text-3xl md:text-4xl lg:text-5xl font-bold font-poppins bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent"
                    data-aos="fade-up"
                  >
                    Contato
                  </h2>
                  <p 
                    className="text-gray-300 dark:text-gray-600 text-lg max-w-2xl mx-auto"
                    data-aos="fade-up"
                    data-aos-delay="100"
                  >
                    Tem um projeto em mente? Vamos conversar! Preencha o formulário abaixo ou entre em contato através das redes sociais.
                  </p>
                </div>

                <div className="grid md:grid-cols-2 gap-12">
                  <form 
                    onSubmit={handleSubmit} 
                    className="space-y-6"
                    data-aos="fade-right"
                    data-aos-delay="200"
                  >
                    <div>
                      <label htmlFor="name" className="block text-sm font-medium mb-2 text-gray-300 dark:text-gray-700">
                        Nome
                      </label>
                      <input 
                        type="text" 
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        className={`w-full p-4 bg-neutral-800/90 dark:bg-gray-200/90 border ${
                          errors.name ? 'border-red-500' : 'border-neutral-600 dark:border-gray-300'
                        } rounded-lg focus:ring-2 focus:ring-purple-500 outline-none text-gray-100 dark:text-gray-900`}
                        placeholder="Seu nome"
                      />
                      {errors.name && (
                        <p className="mt-1 text-sm text-red-500">{errors.name}</p>
                      )}
                    </div>

                    <div>
                      <label htmlFor="email" className="block text-sm font-medium mb-2 text-gray-300 dark:text-gray-700">
                        Email
                      </label>
                      <input 
                        type="email" 
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className={`w-full p-4 bg-neutral-800/90 dark:bg-gray-200/90 border ${
                          errors.email ? 'border-red-500' : 'border-neutral-600 dark:border-gray-300'
                        } rounded-lg focus:ring-2 focus:ring-purple-500 outline-none text-gray-100 dark:text-gray-900`}
                        placeholder="seu@email.com"
                      />
                      {errors.email && (
                        <p className="mt-1 text-sm text-red-500">{errors.email}</p>
                      )}
                    </div>

                    <div>
                      <label htmlFor="subject" className="block text-sm font-medium mb-2 text-gray-300 dark:text-gray-700">
                        Assunto
                      </label>
                      <input 
                        type="text" 
                        id="subject"
                        name="subject"
                        value={formData.subject}
                        onChange={handleChange}
                        className={`w-full p-4 bg-neutral-800/90 dark:bg-gray-200/90 border ${
                          errors.subject ? 'border-red-500' : 'border-neutral-600 dark:border-gray-300'
                        } rounded-lg focus:ring-2 focus:ring-purple-500 outline-none text-gray-100 dark:text-gray-900`}
                        placeholder="Assunto da mensagem"
                      />
                      {errors.subject && (
                        <p className="mt-1 text-sm text-red-500">{errors.subject}</p>
                      )}
                    </div>

                    <div>
                      <label htmlFor="message" className="block text-sm font-medium mb-2 text-gray-300 dark:text-gray-700">
                        Mensagem
                      </label>
                      <textarea 
                        id="message"
                        name="message"
                        value={formData.message}
                        onChange={handleChange}
                        rows="6"
                        className={`w-full p-4 bg-neutral-800/90 dark:bg-gray-200/90 border ${
                          errors.message ? 'border-red-500' : 'border-neutral-600 dark:border-gray-300'
                        } rounded-lg focus:ring-2 focus:ring-purple-500 outline-none text-gray-100 dark:text-gray-900 resize-none`}
                        placeholder="Sua mensagem..."
                      ></textarea>
                      {errors.message && (
                        <p className="mt-1 text-sm text-red-500">{errors.message}</p>
                      )}
                    </div>

                    {submitStatus && (
                      <div className={`p-4 rounded-lg ${
                        submitStatus.type === 'success' 
                          ? 'bg-green-500/10 text-green-500' 
                          : 'bg-red-500/10 text-red-500'
                      }`}>
                        {submitStatus.message}
                      </div>
                    )}

                    <button 
                      type="submit"
                      disabled={isSubmitting}
                      className="w-full py-4 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all hover:scale-105 shadow-lg hover:shadow-purple-500/20 disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100"
                    >
                      {isSubmitting ? (
                        <div className="flex items-center justify-center gap-2">
                          <div className="w-5 h-5 border-t-2 border-b-2 border-white rounded-full animate-spin"></div>
                          Enviando...
                        </div>
                      ) : (
                        'Enviar Mensagem'
                      )}
                    </button>
                  </form>

                  <div 
                    className="space-y-8"
                    data-aos="fade-left"
                    data-aos-delay="300"
                  >
                    <div className="text-center md:text-left">
                      <h3 className="text-2xl font-bold mb-4 bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent">
                        Vamos Conversar!
                      </h3>
                      <p className="text-gray-300 dark:text-gray-600">
                        Estou sempre aberto a novas oportunidades e colaborações. Entre em contato através das redes sociais ou preencha o formulário.
                      </p>
                    </div>

                    <div className="space-y-4">
                      <a 
                        href="https://linkedin.com/in/ronaldo-chiavegatti" 
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center gap-4 p-4 bg-neutral-800/90 dark:bg-gray-200/90 border border-neutral-600 dark:border-gray-300 rounded-lg hover:border-purple-500 transition-all group"
                      >
                        <div className="w-10 h-10 flex items-center justify-center rounded-full bg-neutral-700/90 dark:bg-gray-300/90 text-purple-500 group-hover:scale-110 transition-transform">
                          <i className="fa-brands fa-linkedin-in text-xl"></i>
                        </div>
                        <div>
